import React , {useState} from "react";
import {ClickAwayListener, Drawer, List,ListItem,ListItemText,Typography} from "@material-ui/core"
import { MdHome } from "react-icons/md";
import { IconContext } from "react-icons/lib";
import "./Menu.css";
import { useHistory } from "react-router-dom";
import { BsPersonSquare } from "react-icons/bs";


function Menu() {
    const history = useHistory();
    const[currentPage,setCurrentPage] = useState("/home");

    function handleClick(pathName){
        history.push(pathName);
        setCurrentPage(pathName);
    }

const pages=[
    {
    pathName:"/home",
    icon: <MdHome />,
    text: "Home"

},
{
    pathName:"/perfil",
    icon: <BsPersonSquare/>,
    text: "perfil"

},
];
   return( <Drawer open={true}>
       <List className="list">
           {pages.map((listItem)=>{
        <ListItem 
        button 
        selected={currentPage === listItem.pathName}
        onClick={() => {
            handleClick(listItem.pathName);
        }}>
        <IconContext.Provider value={{color:"#000000", size:"1.5em"}}>
        {listItem.icon}
        </IconContext.Provider>
        <ListItemText className="listItemText">
            <Typography>{listItem.text}</Typography>
        </ListItemText>
        </ListItem>
         })}
       </List>

    </Drawer>

   );
    
}

export default Menu;
