import React , {useState} from "react";
import {ToolBar,IconButton,AppBar, ClickAwayListener, Drawer, List,ListItem,ListItemText,Typography} from "@material-ui/core"
import { MdHome } from "react-icons/md";
import { IconContext } from "react-icons/lib";
import "./Menu.css";
import { useHistory } from "react-router-dom";
import { BsPersonSquare } from "react-icons/bs";
import {ImBooks} from "react-icons/im";
import {FiMenu} from "react-icons/fi"
function Menu() {
    const history = useHistory();
    const[currentPage,setCurrentPage] = useState("/home");

    function handleClick(pathName){
        history.push(pathName);
        setCurrentPage(pathName);
    }

const pages=[
    {
    pathName:"/home",
    icon: <MdHome />,
    text: "Home",
    iconSize: "1.5em"

},
{
    pathName:"/perfil",
    icon: <BsPersonSquare/>,
    text: "Perfil",
    iconSize: "1.3em"

},
{   
    pathName:"/biblioteca",
    icon: <ImBooks/>,
    text: "Biblioteca",
    iconSize:"1.5em"
}
];
   return(
       <>
       <AppBar position="static">
        <Toolbar>
        <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            
          </IconButton> 


         <FiMenu/>
        </Toolbar>
        

       </AppBar>
       
       
       <Drawer open={true}>
       <List className="list">
           {pages.map((listItem)=>{
               return (
        <ListItem 
        button 
        selected={currentPage === listItem.pathName}
        onClick={() => {
            handleClick(listItem.pathName);
        }}>
        <IconContext.Provider value={{color:"#000000", size:listItem.iconSize}}>
        {listItem.icon}
        </IconContext.Provider>
        <ListItemText className="listItemText">
            <Typography>{listItem.text}</Typography>
        </ListItemText>
        </ListItem>
            ); })}
       </List>

    </Drawer>
   </>
   );
    
}

export default Menu;
